mapper.select:
/* home.checkUsername : 아이디 중복 여부 체크용 */
SELECT
	USERNAME
FROM USR
WHERE USERNAME = #{username}

mapper.insert:
/* home.insertNewUser : 신규 회원 정보 저장(개인 회원) */
INSERT INTO USR
(
	ID
	, USERNAME
	, PASSWORD
	, FIRST_NAME
	, MIDDLE_NAME
	, LAST_NAME
	, PHONE
	, EMAIL
	, NATIONALITY
	, REG_DATE
	, ROLE_V
) VALUES
(
	#{id}
	, #{username}
	, #{password}
	, #{first_name}
	, #{middle_name}
	, #{last_name}
	, #{phone}
	, #{email}
	, #{nationality}
	, SYSDATE()
	, 'USER'
)

/* home.insertNewUser2 : 신규 회원 정보 저장(기업 회원) */
INSERT INTO USR
(
	ID
	, USERNAME
	, PASSWORD
	, FIRST_NAME
	, MIDDLE_NAME
	, LAST_NAME
	, PHONE
	, EMAIL
	, NATIONALITY
	, REG_DATE
	, ROLE_V
) VALUES
(
	#{id}
	, #{username}
	, #{password}
	, #{first_name}
	, #{middle_name}
	, #{last_name}
	, #{phone}
	, #{email}
	, #{nationality}
	, SYSDATE()
	, 'COMP'
)

/* home.insertNewCompany : 신규 기업정보 저장 */
INSERT INTO COMPANY
(
	ID
	, COMPNAY_NAME
	, USR_ID
	, WEBSITE
	, CEO
) VALUES
(
	#{id}
	, #{company_name}
	, #{usr_id}
	, #{website}
	, #{ceo}
)
