mapper.select:
/* home.checkUsername : 아이디 중복 여부 체크용 */
SELECT
	USERNAME
FROM USR
WHERE USERNAME = #{username}

mapper.insert:
/* home.insertNewUser : 신규 회원 정보 저장 */
INSERT INTO USR
(
	ID
	, USERNAME
	, PASSWORD
	, FIRST_NAME
	, MIDDLE_NAME
	, LAST_NAME
	, PHONE
	, EMAIL
	, NATIONALITY
	, REG_DATE
	, ROLE_V
) VALUES
(
	#{id}
	, #{username}
	, #{password}
	, #{first_name}
	, #{middle_name}
	, #{last_name}
	, #{phone}
	, #{email}
	, #{nationality}
	, SYSDATE()
	, 'USER'
)

mapper.select:
/* home.getInfoById : 로그인 정보 검증 */
SELECT
	USERNAME
	, PASSWORD
FROM USR
WHERE USERNAME = #{username}
