mapper.select:
/* chatting.checkUsername : 아이디 중복 여부 체크용 */
SELECT
	USERNAME
FROM USR
WHERE USERNAME = #{username}

mapper.insert:
/* chatting.insertNewUser : 신규 회원 정보 저장 */
INSERT INTO USR
(
	ID
	, USERNAME
	, PASSWORD
	, REG_DATE
) VALUES
(
	#{id}
	, #{username}
	, #{password}
	, SYSDATE()
)

mapper.select:
/* chatting.getInfoById : 로그인 정보 검증 */
SELECT
	USERNAME
	, PASSWORD
FROM USR
WHERE USERNAME = #{username}
