mapper.select:
/* resume.getCareerListCnt : 해당 이력서 header 값에 해당하는 모든 경력사항을 count. */

SELECT
	COUNT(*)
FROM CAREER
WHERE RESUME_ID = #{resume_id}

/* resume.getAcademicListCnt : 해당 이력서 header 값에 해당하는 모든 학력사항을 count. */

SELECT
	COUNT(*)
FROM ACADEMIC
WHERE RESUME_ID = #{resume_id}

mapper.insert:
/* resume.insertBasicInfo : 이력서 기본정보를 insert */
INSERT INTO RESUME
(
	ID
	, USR_ID
	, TITLE
	, SUMMARY
	, REMARK
	, REG_DATE
) 
VALUES 
(
	#{id}
	, #{usr_id}
	, #{title}
	, #{summary}
	, #{remark}
	, SYSDATE()
)

/* resume.insertCareerInfo : 이력서 경력정보를 insert */
INSERT INTO CAREER
(
	ID
	, RESUME_ID
	, START_DATE
	, END_DATE
	, COMPANY_NAME
	, COUNTRY
	, CITY
	, JOB_DUTY
	, POSI
	, REG_DATE
)
VALUES
(	
	#{id}
	, #{resume_id}
	, #{start_date}
	, #{end_date}
	, #{company_name}
	, #{country}
	, #{city}
	, #{job_duty}
	, #{posi}
	, SYSDATE()
)

/* resume.insertAcademicInfo : 이력서 학력정보를 insert */
INSERT INTO ACADEMIC
(
	ID
	, RESUME_ID
	, START_DATE
	, END_DATE
	, STILL_YN
	, SCHOOL_NAME
	, COUNTRY
	, CITY
	, MAJOR
	, GRADE
	, REG_DATE
)
VALUES
(
	#{id}
	, #{resume_id}
	, #{start_date}
	, #{end_date}
	, #{still_yn}
	, #{school_name}
	, #{country}
	, #{city}
	, #{major}
	, #{grade}
	, SYSDATE()
)

mapper.update:
/* resume.updateBasicInfo : 이력서 기본정보를 update */
UPDATE RESUME SET
	TITLE = #{title}
	, SUMMARY = #{summary}
	, REMARI = #{remark}
	, MOD_DATE = SYSDATE()
WHERE ID = #{id}
AND USR_ID = #{usr_id}

/* resume.updateCareerInfo : 이력서 경력정보를 update */
UPDATE CAREER SET
	START_DATE = #{start_date}
	, END_DATE = #{end_date}
	, COMPANY_NAME = #{company_name}
	, COUNTRY = #{country}
	, CITY = #{city}
	, JOB_DUTY = #{job_duty}
	, POSI = #{posi}
	, MOD_DATE = SYSDATE()
WHERE ID = #{id}
AND RESUME_ID = #{resume_id}

/* resume.updateAcademicInfo : 이력서 학력정보를 update */
UPDATE ACADEMIC SET
	START_DATE = #{start_date}
	, END_DATE = #{end_date}
	, STILL_YN = #{still_yn}
	, SCHOOL_NAME = #{school_name}
	, COUNTRY = #{country}
	, CITY = #{city}
	, MAJOR = #{major}
	, GRADE = #{grade}
	, MOD_DATE = SYSDATE()
WHERE ID = #{id}
AND RESUME_ID = #{resume_id}

mapper.delete:
/* resume.deleteBasicInfo : 이력서 기본정보를 delete */
DELETE FROM RESUME
WHERE ID = #{id}
AND USR_ID = #{usr_id}

/* resume.deleteCareerList : 이력서 경력정보 목록을 delete */
DELETE FROM CAREER
WHERE RESUME_ID = #{resume_id}

/* resume.deleteAcademicList : 이력서 학력정보 목록을 delete */
DELETE FROM ACADEMIC
WHERE RESUME_ID = #{resume_id}

/* resume.deleteCareerList : 이력서 경력정보를 delete */
DELETE FROM CAREER
WHERE ID = #{id}
AND RESUME_ID = #{resume_id}

/* resume.deleteCareerList : 이력서 학력정보를 delete */
DELETE FROM ACADEMIC
WHERE ID = #{id}
AND RESUME_ID = #{resume_id}
